import React, { Component } from 'react'
import './App.css' // Você precisará criar um arquivo App.css para estilos
import { Container, ContainerPad } from './styles'

interface AppState {
  msg: string
  pad_c: boolean
  pad_d: boolean
  pad_e: boolean
}

// eslint-disable-next-line @typescript-eslint/ban-types
class App extends Component<{}, AppState> {
  player_pad_c: HTMLAudioElement
  player_pad_d: HTMLAudioElement
  player_pad_e: HTMLAudioElement

  // eslint-disable-next-line @typescript-eslint/ban-types
  constructor(props: {}) {
    super(props)
    this.state = {
      msg: '',
      pad_c: false,
      pad_d: false,
      pad_e: false
    }
    this.player_pad_c = new Audio()
    this.player_pad_d = new Audio()
    this.player_pad_e = new Audio()
  }

  componentDidMount() {
    this.player_pad_c.src = require('./foundations/c.mp3')
    this.player_pad_d.src = require('./foundations/d.mp3')
    this.player_pad_e.src = require('./foundations/e.mp3')
  }

  _play = (pad: string) => {
    const { pad_c, pad_d, pad_e } = this.state
    this._stopAll()

    // Defina o tempo em segundos para o fade in
    const fadeInDuration = 2

    switch (pad) {
      case 'C':
        this.setState({ pad_c: true })
        this.player_pad_c.volume = 0 // Comece com volume zero
        this.player_pad_c.play()
        this._fadeIn(this.player_pad_c, fadeInDuration)
        break
      case 'D':
        this.setState({ pad_d: true })
        this.player_pad_d.volume = 0 // Comece com volume zero
        this.player_pad_d.play()
        this._fadeIn(this.player_pad_d, fadeInDuration)
        break
      case 'E':
        this.setState({ pad_e: true })
        this.player_pad_e.volume = 0 // Comece com volume zero
        this.player_pad_e.play()
        this._fadeIn(this.player_pad_e, fadeInDuration)
        break
      default:
        break
    }
  }

  _stop = (pad: string) => {
    switch (pad) {
      case 'C':
        this.setState({ pad_c: false })
        this.player_pad_c.pause()
        this.player_pad_c.currentTime = 0
        break
      case 'D':
        this.setState({ pad_d: false })
        this.player_pad_d.pause()
        this.player_pad_d.currentTime = 0
        break
      case 'E':
        this.setState({ pad_e: false })
        this.player_pad_e.pause()
        this.player_pad_e.currentTime = 0
        break
      default:
        break
    }
  }

  _stopAll = () => {
    this.setState({ pad_c: false, pad_d: false, pad_e: false })
    this.player_pad_c.pause()
    this.player_pad_c.currentTime = 0
    this.player_pad_d.pause()
    this.player_pad_d.currentTime = 0
    this.player_pad_e.pause()
    this.player_pad_e.currentTime = 0
  }

  // Função para aplicar fade in gradualmente
  _fadeIn = (player: HTMLAudioElement, duration: number) => {
    const increment = 0.05 // Incremento de volume a cada intervalo de tempo
    const interval = (duration * 1000) / (player.volume / increment) // Intervalo em milissegundos
    const fadeInInterval = setInterval(() => {
      if (player.volume + increment < 1) {
        player.volume += increment
      } else {
        clearInterval(fadeInInterval) // Limpar o intervalo quando o volume atingir 1
      }
    }, interval)
  }

  render() {
    const { pad_c, pad_d, pad_e } = this.state
    return (
      <div>
        <Container>
          <ContainerPad
            className={pad_c ? 'pad active' : 'pad'}
            onClick={() => {
              this._play('C')
            }}
          >
            <img src="assets/play.png" alt="Play" />
            <p>Paradise C</p>
          </ContainerPad>
          <ContainerPad
            className={pad_d ? 'pad active' : 'pad'}
            onClick={() => {
              this._play('D')
            }}
          >
            <img src="assets/play.png" alt="Play" />
            <p>Paradise D</p>
          </ContainerPad>
          <ContainerPad
            className={pad_e ? 'pad active' : 'pad'}
            onClick={() => {
              this._play('E')
            }}
          >
            <img src="assets/play.png" alt="Play" />
            <p>Paradise E</p>
          </ContainerPad>
        </Container>
        <div className="control-panel">
          <p>Pad C: {pad_c.toString()}</p>
          <p>Pad D: {pad_d.toString()}</p>
          <p>Pad E: {pad_e.toString()}</p>
          {(pad_c || pad_d || pad_e) && (
            <img src="assets/stop.png" alt="Stop" onClick={this._stopAll} />
          )}
        </div>
      </div>
    )
  }
}

export default App
