import styled from 'styled-components'

export const Title = styled.div`
  font-size: 32px;
  font-weight: bold;

  p {
    color: #d9d9d9;
    display: inline;
  }
  margin-bottom: 32px;
`

export const Square = styled.div`
  max-width: 300px;
  height: 180px;
  background-color: #d9d9d9;
  position: relative; /* Necessário para o posicionamento absoluto da imagem */
`

export const ImageProduct = styled.img`
  position: absolute;
  width: 100%;
  height: 100%;
  object-fit: cover; /* Ajusta a imagem para se ajustar ao contêiner */
`
export const ContainerImageItemProduct = styled.div`
  width: 300px;
  height: 180px;
  position: relative;
  overflow: hidden; /* Adicionado para evitar que a imagem ultrapasse o contêiner */
`
export const ContainerItemProduto = styled.div`
  display: flex; /* Mudado para flex */
  flex-direction: column; /* Organiza os elementos em colunas */
  align-items: center; /* Alinha os elementos horizontalmente */
  justify-content: center; /* Alinha os elementos verticalmente */
  max-width: 100%;
  height: 350px;
  position: relative;
  overflow: hidden; /* Adicionado para evitar que os elementos ultrapassem o contêiner */
`
